package com.lelocabdriver.utils;import android.content.Context;import android.content.SharedPreferences;import java.io.IOException;/** * Created by Admin on 9/11/2015. */public class Prefs {    private static final String PREFS_NAME = "com.lelocabdriver.prefs";    public static boolean getBoolean(Context c, String key) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        return settings.getBoolean(key, false);    }    public static void setBoolean(Context c, String key, boolean value) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        SharedPreferences.Editor editor = settings.edit();        editor.putBoolean(key, value);        editor.commit();    }    public static boolean getFirstTime(Context c) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        return settings.getBoolean("app_first_time", true);    }    public static void setFirstTimeEnd(Context c) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        SharedPreferences.Editor editor = settings.edit();        editor.putBoolean("app_first_time", false);        editor.commit();    }    public static String getString(Context c, String key) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        return settings.getString(key, "");    }    public static void setString(Context c, String key, String value) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        SharedPreferences.Editor editor = settings.edit();        editor.putString(key, value);        editor.commit();    }    public static void setGcmToken(String gcmToken, Context c) {        SharedPreferences sp = c.getSharedPreferences(PREFS_NAME, 0);        SharedPreferences.Editor edit = sp.edit();        edit.putString("gcmToken", gcmToken);        edit.commit();    }    public static String getGcmToken(Context c) {        SharedPreferences sp = c.getSharedPreferences(PREFS_NAME, 0);        return sp.getString("gcmToken", "");    }    /**     * This genric method use to put object into preference<br>     * How to use<br>     * Bean bean = new Bean();<br>     * putObjectIntoPref(context,bean,key)     *     * @param context Context of an application     * @param e       your genric object     * @param key     String key which is associate with object     */    public static <E> void putObjectIntoPref(Context context, E e, String key) {        SharedPreferences.Editor editor = context.getSharedPreferences("appPref",                Context.MODE_PRIVATE).edit();        try {            editor.putString(key, ObjectSerializer.serialize(e));        } catch (IOException exc) {            exc.printStackTrace();        }        editor.commit();    }    /**     * This method is use to get your object from preference.<br>     * How to use<br>     * Bean bean = getObjectFromPref(context,key);     *     * @param context     * @param key     * @return     */    @SuppressWarnings("unchecked")    public static <E> E getObjectFromPref(Context context, String key) {        try {            SharedPreferences pref = context.getSharedPreferences("appPref",                    Context.MODE_PRIVATE);            return (E) ObjectSerializer.deserialize(pref.getString(key, null));        } catch (Exception e) {            e.printStackTrace();            return null;        }    }    public static long getLong(Context c, String key) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        return settings.getLong(key, 0L);    }    public static void setLong(Context c, String key, long value) {        SharedPreferences settings = c.getSharedPreferences(PREFS_NAME, 0);        SharedPreferences.Editor editor = settings.edit();        editor.putLong(key, value);        editor.commit();    }}